// function to serialise the items again in case of removal of items
function properSerials(){
		j= 0; 
		for (j=1; j <= finalDataForPdf.length ; j++){
		text= ""+j+".";
		finalDataForPdf[j][0] = {text}
		};
};
function generatePDF(){
	//var requester = ""+$('#Name').val();
	var filename = "LibRequest_Requests-"+requester+"-"+todaysDateFormatted;
	var requesterName = "Name of the Faculty member: "+requester;
	var requesterEmail = "Email-id: "+requesteremail;
	docDefinition = {
			// a string or { width: number, height: number }
			pageSize: 'A4',
			//background: 'simple text',

			// by default we use portrait, you can change it to landscape if you wish
			pageOrientation: 'landscape',

			// [left, top, right, bottom] or [horizontal, vertical] or just a number for equal margins
			pageMargins: [ 40, 60, 40, 60 ],
			info: {
		    title: 'Library Request List',
		    author: 'Vinit Kumar',
		    subject: 'LibRequest',
		    keywords: 'LibRequest',
			},
			header: {columns: [{text: 'Generated through LibRequest Service available at: ', fontSize: 8}, {text: reportDate, fontSize: 8, alignment: 'right'}]},
			footer: {columns: [{text: '(Head of Department)', margin: [5, 0, 0, 0]},{ text: '(Faculty)', alignment: 'right',margin: [0, 10, 0, 0] }]},
			content: [{columns: [{image: 'data:image/jpeg;base64,',
						width: 75,
						height: 70,
						margin: [0, 0, 0, 10]
					}, [{text: university, style: 'title'},{text: 'Kanpur Road, Jhansi', style: 'subtitle'}, {text: '284128', style: 'subtitle'}]]}, 
			{text: requesterName, style: 'field'}, {text: requesterEmail, style: 'field'},
			{table: {
						headerRows: 1, widths: [ 'auto','*', 'auto', 'auto', 'auto','auto','auto' ],
				    	body: finalDataForPdf
			  		}
			}], 
			styles: {
					field: {
						fontSize: 12,
						alignment: 'Left',
						margin: [0, 0, 0, 10]
					},
					title: {
						fontSize: 18,
						bold: true,
						alignment: 'center'
					}, 
					subtitle: {
						fontSize: 14,
						bold: true,
						alignment: 'center'
					}
			}
		};
	var folder = 'LibRequest';
	var appDirectory;

		//window.resolveLocalFileSystemURL(cordova.file.externalApplicationStorageDirectory, function(dir) { 
        window.resolveLocalFileSystemURL(cordova.file.externalApplicationStorageDirectory, function(dir) {
        dir.getDirectory(folder, {create:true, exclusive: false}, function(directory){
        appDirectory = directory;
        console.log("App directory initialized." + appDirectory);
	    	}, fail);
	    }, fail);

		function fail(error) {
		     console.log("App directory Error: "+error.code);
		}

		pdfMake.createPdf(docDefinition).getBuffer(function (buffer) {
		        var utf8 = new Uint8Array(buffer); // Convert to UTF-8...                
		        binaryArray = utf8.buffer; // Convert to Binary...
		        save(binaryArray, filename+'.pdf')
		});

		function save(data,savefile){
		    var pdffile = savefile;
		    appDirectory.getFile(pdffile, {create:true, exclusive: false}, function(fileEntry){
		        fileEntry.createWriter(win,fail);
		    }, fail);

		    function win(writer) {
		        writer.onwriteend = function(evt) {
		            if (writer.length === 0) {
		                writer.write(data);
		            } else { }
		        };
		        writer.truncate(0);
		        console.log("Write success");
		        alert("Saving is successful.")
		    };

		    function fail(error) {
		        console.log("Writer Error: "+error.code);
		    }
		}
}; // end function generatePDF
